<?phpclass Cutpage{	private $sumpage;//总页码	private $sumnews;//文章总数	private $shownews;//每页显示文章数 		public function __construct($sumnews,$shownews){		$this->sumnews = (int)$sumnews;		$this->shownews= (int)$shownews;		$this->sumpage = $this->process();	}	public static function process($sumnews,$shownews){		$sumnews = (int)$sumnews;		$shownews= (int)$shownews;		if($sumnews){			if($sumnews < $shownews)				return (int)1;			if($sumnews%$shownews)				return (int)($sumnews/$shownews)+1;			else return (int)$sumnews/$shownews;		}		else return (int)0;	}	public function getpage(){		return $this->sumpage;	}}?>